package ushapackage;

public class Check_leap_year {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int input_year = 2019;
		if (input_year%4 == 0)
		{
			System.out.println("Given year is leap year");
		}else 
		{
			System.out.println("Given year is NOT leap year");
		}
	}

}
